from Crypto.Util.number import isPrime, getStrongPrime
from gmpy2 import next_prime
from secret import flag2

p = getStrongPrime(1024)
q = next_prime(p ^ ((1<<1024)-1))
n = p * q
e = 65537
 
# Encryption
m = int(flag2.encode('hex'),16)
assert m < n
c = pow(m, e, n)
 
print('n = {}'.format(hex(n)))
